import React, { useEffect, useContext, useState } from "react";
import './Navbar.css';
import { ProjectContext } from "../../contexts/project.context";
import NotificationsIcon from '@mui/icons-material/Notifications';
import LogoutIcon from '@mui/icons-material/Logout';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import submitz from '../../assets/submitz.png';
import { UserContext } from "../../contexts/user.context";
import Dropdown from "./Dropdown/Dropdown";
import DropdownButton from "./Dropdown/DropdownButton";
import NewForm from "./NewForm";
import axios from 'axios';
import io from 'socket.io-client';
import NotificationPopover from "./Notifications";
import DropdownContent from "./components/Navbar/Dropdown/DropdownContent";
import DropdownList from "./components/Navbar/Dropdown/DropdownList";
import DropdownItem from "./components/Navbar/Dropdown/DropdownItem";

const Navbar = () => {
  const { logOutUser } = useContext(UserContext);
  const { projects, selectedProject, setSelectedProject, fetchProjects } = useContext(ProjectContext);
  const [showNotifications, setShowNotifications] = useState(false);

  const handleDropdownChange = (selectedOption) => {
    if (selectedOption) {
      setSelectedProject(selectedOption.value);
    } else {
      setSelectedProject(null);
    }
  }

  const refreshProjects = async () => {
    // Fetch the updated list of projects
    await fetchProjects();
  }

  useEffect(() => {
    console.log('SelectedProject in Navbar: ', selectedProject);
    console.log('setSelectedProject in Navbar: ', setSelectedProject);
    fetchProjects(); // Fetch the initial list of projects
  }, [selectedProject]);

  // Potential notifications feature
  const toggleNotifications = () => {
    setShowNotifications(!showNotifications);
  }

  return (
    <div className='navbar-container'>
      <div className='logo'>
        <img src={submitz} alt='logo' />
        <div className='dropdown-container'>
          <Dropdown>
            <DropdownButton>Projects</DropdownButton>
            <DropdownContent>
              <DropdownList>
                <DropdownItem>Dropdown Menu Item 1</DropdownItem>
                <DropdownItem>Dropdown Menu Item 2</DropdownItem>
                <DropdownItem>Dropdown Menu Item 3</DropdownItem>
                <DropdownItem>Dropdown Menu Item 4</DropdownItem>
                <DropdownItem>Dropdown Menu Item 5</DropdownItem>
                <DropdownItem>Dropdown Menu Item 6</DropdownItem>
                <DropdownItem>Dropdown Menu Item 7</DropdownItem>
                <DropdownItem>Dropdown Menu Item 8</DropdownItem>
              </DropdownList>
            </DropdownContent>
          </Dropdown>
        </div>
      </div>
      <div className='nav'>
        <p><AddCircleOutlineIcon />New Form</p>
        <p onClick={toggleNotifications}><NotificationsIcon /></p>
        <p><LogoutIcon onClick={logOutUser} /></p>
      </div>
      {showNotifications && (
        <NotificationPopover />
      )}
    </div>
  )
}

export default Navbar;
